import React, { useState } from 'react';
import './App.css';

function plusCount (){
  setCount(count + 1);
}

function App() {
  const [count, setCount] = useState(0);

  return (
    <div className="App">
      <header className="App-header">
        <section class="couner-container">
          {count}
          <div>
          <button onClick={plusCount} >+</button>
          <button onClick={() => setCount(count - 1)}>-</button>
        </div>
        </section>
      </header>
    </div>
  );
}

export default App;
